/*
 * The different types of strings that are currently supported. This is only
 * needed by method authors.
 */
enum ossl_UI_string_types {
    ossl_UIT_NONE = 0,
    ossl_UIT_PROMPT,                 /* Prompt for a string */
    ossl_UIT_VERIFY,                 /* Prompt for a string and verify */
    ossl_UIT_BOOLEAN,                /* Prompt for a yes/no response */
    ossl_UIT_INFO,                   /* Send info to the user */
    ossl_UIT_ERROR                   /* Send an error message to the user */
};

